POU.Declaration:
PROGRAM MAIN VAR iCounter AT %Q* : DINT;
(* instance of axes *) fbAxis1 : FB_Axis; // 1st axis instance lrActPos1 : LREAL; // 1st axis position fbAxis2 : FB_Axis; // 2nd axis instance lrActPos2 : LREAL; // 2nd axis position
(* common *) bEnabled AT %Q* : BOOL; // all axes enabled bError AT %Q* : BOOL; // at leas one axis in error condition bDriveReset AT %I* : BOOL; // request reset bHalt AT %I* : BOOL; // request halt END_VAR

Implementation.ST:
iCounter := iCounter + 1;

fbAxis1(
	bDriveReset := bDriveReset,
	bHalt 		:= bHalt,
	lrActPos	=> lrActPos1
);

fbAxis2(
	bDriveReset := bDriveReset,
	bHalt 		:= bHalt,
	lrActPos	=> lrActPos2
);

bEnabled	:= fbAxis1.bStatus AND fbAxis2.bStatus;
bError		:= fbAxis1.bDriveError OR fbAxis1.bNcError OR fbAxis2.bNcError OR fbAxis2.bDriveError;
