POU.Declaration:
PROGRAM MAIN_ST
VAR
	Timer			: TON;
	fbST_SamplePOU 	: FB_ST_SamplePOU;

	eOperation		: E_Operation := eOp_Add;
	iResultC		: DINT;
	bZero AT %Q*	: BOOL;
	bPos  AT %Q*	: BOOL;
	bNeg  AT %Q*	: BOOL;
	bTest AT %Q*	: BOOL;
	tTimerValue		: TIME := T#3S;
END_VAR

Implementation.ST:
bTest := (((bZero AND bPos) OR (bPos XOR bNeg)) AND (Timer.ET > TIME#500MS) AND (iResultC <= 0)) OR (Timer.ET <= TIME#500MS);


(* Timer *)
Timer(IN := TRUE, PT := tTimerValue);
IF Timer.Q THEN
	Timer(IN := FALSE);

	(* State machine *)
	CASE eOperation OF
	eOp_Add:
		eOperation := eOp_MUL;
	eOp_Sub:
		eOperation := eOp_ADD;
	eOp_Mul:
		eOperation := eOp_SUB;
	END_CASE
END_IF

(* FB Call *)
fbST_SamplePOU(
	eOperation	:= eOperation,
	iResultC	=> iResultC
);

(* Evaluation *)
IF fbST_SamplePOU.iResultC = 0 THEN
	bZero := TRUE;
	bPos := FALSE;
	bNeg := FALSE;
ELSIF fbST_SamplePOU.iResultC > 0 THEN
	bZero := FALSE;
	bPos := TRUE;
	bNeg := FALSE;
ELSIF fbST_SamplePOU.iResultC < 0 THEN
	bZero := FALSE;
	bPos := FALSE;
	bNeg := TRUE;
END_IF
